import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import { Button } from "./components/ui/button"
import { Input } from "./components/ui/input"
import { Textarea } from "./components/ui/textarea"
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "./components/ui/card"
import { Alert, AlertDescription, AlertTitle } from "./components/ui/alert"
import { Progress } from "./components/ui/progress"
import { Mic, Upload, Download, AlertCircle, CheckCircle2 } from 'lucide-react'

const supabase = createClient('https://qgpgtosfopmnkrzcirfe.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InFncGd0b3Nmb3BtbmtyemNpcmZlIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzE3Nzg2NzgsImV4cCI6MjA0NzM1NDY3OH0.w3M3ISoXlm2Ls6AFOQzPUOTuykYzr3xTLPYHkdd28bE');

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5MB
const ALLOWED_FILE_TYPES = ['audio/wav', 'audio/mpeg', 'audio/mp3', 'audio/mp4'];

export default function App() {
  const [voiceFile, setVoiceFile] = useState(null);
  const [text, setText] = useState('');
  const [generatedAudioUrl, setGeneratedAudioUrl] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);

  const validateFile = (file) => {
    if (!file) return 'Please select a file';
    if (!ALLOWED_FILE_TYPES.includes(file.type)) {
      return 'Invalid file format. Please upload an audio file (WAV, MP3, or MP4)';
    }
    if (file.size > MAX_FILE_SIZE) {
      return 'File is too large. Maximum size is 5MB';
    }
    return null;
  };

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      const error = validateFile(file);
      if (error) {
        setError(error);
        setVoiceFile(null);
        event.target.value = '';
      } else {
        setVoiceFile(file);
        setError('');
        setSuccess('File uploaded successfully!');
      }
    }
  };

  const handleTextChange = (event) => {
    const newText = event.target.value;
    if (newText.length <= 500) {
      setText(newText);
      setError('');
    }
  };

  const simulateProgress = () => {
    setProgress(0);
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          return 100;
        }
        return prev + 10;
      });
    }, 500);
    return interval;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    if (!voiceFile) {
      setError('Please upload a voice file');
      setIsLoading(false);
      return;
    }
    if (!text.trim()) {
      setError('Please enter some text');
      setIsLoading(false);
      return;
    }

    const progressInterval = simulateProgress();

    try {
      // Upload voice file to Supabase storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('voice-files')
        .upload(`input/${Date.now()}-${voiceFile.name}`, voiceFile);

      if (uploadError) throw new Error('Failed to upload voice file');

      // Call backend API to handle voice synthesis
      const response = await fetch('http://localhost:3001/api/synthesize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          voiceFileUrl: uploadData.path,
          text: text
        }),
      });

      if (!response.ok) throw new Error('Voice synthesis failed');

      const { audioUrl } = await response.json();
      setGeneratedAudioUrl(audioUrl);
      setSuccess('Voice synthesis completed successfully!');
      clearInterval(progressInterval);
      setProgress(100);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'An unexpected error occurred');
      clearInterval(progressInterval);
      setProgress(0);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      <Card className="w-full">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Mic className="w-6 h-6" />
            Voice Synthesis App
          </CardTitle>
          <CardDescription>
            Upload a voice recording and enter text to generate synthesized speech
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-2">
              <label htmlFor="voiceFile" className="block text-sm font-medium">
                Upload Voice File
              </label>
              <div className="flex items-center gap-4">
                <Input
                  id="voiceFile"
                  type="file"
                  accept={ALLOWED_FILE_TYPES.join(',')}
                  onChange={handleFileUpload}
                  className="file:bg-secondary file:text-secondary-foreground hover:file:bg-secondary/80"
                />
                {voiceFile && (
                  <CheckCircle2 className="w-5 h-5 text-green-500" />
                )}
              </div>
              <p className="text-sm text-muted-foreground">
                Maximum file size: 5MB. Supported formats: WAV, MP3, MP4
              </p>
            </div>

            <div className="space-y-2">
              <label htmlFor="text" className="block text-sm font-medium">
                Enter Text
              </label>
              <Textarea
                id="text"
                value={text}
                onChange={handleTextChange}
                placeholder="Enter the text you want to synthesize (max 500 characters)"
                className="min-h-[100px]"
              />
              <p className="text-sm text-muted-foreground">
                {text.length}/500 characters
              </p>
            </div>

            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertTitle>Error</AlertTitle>
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            {success && (
              <Alert className="bg-green-50 text-green-700 border-green-200">
                <CheckCircle2 className="h-4 w-4" />
                <AlertTitle>Success</AlertTitle>
                <AlertDescription>{success}</AlertDescription>
              </Alert>
            )}

            {isLoading && (
              <div className="space-y-2">
                <Progress value={progress} className="w-full" />
                <p className="text-sm text-center text-muted-foreground">
                  Processing your request... {progress}%
                </p>
              </div>
            )}

            <Button
              type="submit"
              disabled={isLoading}
              className="w-full"
            >
              {isLoading ? 'Processing...' : 'Generate Voice'}
            </Button>
          </form>
        </CardContent>
        {generatedAudioUrl && (
          <CardFooter className="flex flex-col gap-4">
            <div className="w-full">
              <h3 className="text-lg font-semibold mb-2">Preview</h3>
              <audio controls src={generatedAudioUrl} className="w-full" />
            </div>
            <Button
              variant="outline"
              className="w-full"
              onClick={() => {
                const link = document.createElement('a');
                link.href = generatedAudioUrl;
                link.download = 'synthesized-voice.mp3';
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
              }}
            >
              <Download className="w-4 h-4 mr-2" />
              Download Audio
            </Button>
          </CardFooter>
        )}
      </Card>
    </div>
  );
}
