import React, { useState, useRef } from 'react';
import axios from 'axios';

const VoiceSynthesizer = () => {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef(null);
  const audioRef = useRef(null);

  // File validation logic
  const validateFile = (file) => {
    const validTypes = ['audio/wav', 'audio/mpeg', 'audio/mp3', 'audio/mp4'];
    if (!validTypes.includes(file.type)) {
      throw new Error('Invalid file type. Please upload an audio file (WAV, MP3, or MP4).');
    }
    if (file.size > 5 * 1024 * 1024) {
      throw new Error('File size too large. Maximum size is 5MB.');
    }
  };

  // Event handlers remain the same
  const handleFileChange = (event) => {
    try {
      const selectedFile = event.target.files[0];
      if (selectedFile) {
        validateFile(selectedFile);
        setFile(selectedFile);
        setError('');
      }
    } catch (err) {
      setError(err.message);
      setFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  // Check if the generated audio file exists
  const checkFileStatus = async (fileName) => {
    try {
      const response = await axios.get(`http://localhost:3001/api/check-file/${fileName}`);
      console.log('File check response:', response.data); // Log the response

      if (response.data.exists && response.data.url) {
        displayAudio(response.data.url);
        return true;
      }
      return false;
    } catch (err) {
      console.error('Error checking file status:', err);
      return false;
    }
  };

  // Function to display audio
  const displayAudio = (audioUrl) => {
    setAudioUrl(audioUrl);
    setLoading(false);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text || !file) {
      setError('Both text and voice file are required.');
      return;
    }

    setLoading(true);
    setError('');
    setAudioUrl('');
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('voiceFile', file);
    formData.append('text', text);

    try {
      const response = await fetch('http://localhost:3001/api/synthesize', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to synthesize audio');
      }

      const data = await response.json();
      console.log('Data received from API:', data); // Log the response data

      let retries = 0;
      const maxRetries = 10;
      const fileName = data.fileName.split('/').pop();

      const checkFile = async () => {
        if (retries >= maxRetries) {
          throw new Error('Failed to generate audio file after multiple attempts');
        }

        const exists = await checkFileStatus(fileName);
        if (!exists) {
          retries++;
          await new Promise(resolve => setTimeout(resolve, 1000));
          return checkFile();
        }
      };

      await checkFile();

    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  // Reset all states for retry
  const handleRetry = () => {
    setError('');
    setAudioUrl('');
    setFile(null);
    setText('');
    setUploadProgress(0);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h2>Voice Synthesizer</h2>
        </div>
        <div className="card-content">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>
                Upload Voice File (WAV, MP3, or MP4, max 5MB)
              </label>
              <input
                ref={fileInputRef}
                type="file"
                accept=".wav,.mp3,.mp4"
                onChange={handleFileChange}
              />
            </div>

            <div className="form-group">
              <label>
                Text to Synthesize
              </label>
              <textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Enter the text you want to synthesize..."
              />
            </div>

            {error && (
              <div className="alert error">
                <span className="alert-icon">âš </span>
                <div className="alert-content">
                  <h3>Error</h3>
                  <p>{error}</p>
                </div>
              </div>
            )}

            <div className="button-container">
              <button
                type="submit"
                disabled={loading || !file || !text}
                className="submit-button"
              >
                {loading ? (
                  <span className="loading-text">
                    <span className="loader"></span>
                    Processing...
                  </span>
                ) : (
                  <span>
                    Synthesize Audio
                  </span>
                )}
              </button>

              {uploadProgress > 0 && uploadProgress < 100 && (
                <div className="progress-bar">
                  <div
                    className="progress-bar-fill"
                    style={{ width: `${uploadProgress}%` }}
                  ></div>
                </div>
              )}

              {audioUrl && (
                <div className="audio-section">
                  <div className="audio-header">
                    <span className="audio-icon">ðŸ”Š</span>
                    <span>Generated Audio</span>
                  </div>
                  <audio
                    ref={audioRef}
                    controls
                    src={audioUrl}
                  >
                    Your browser does not support the audio element.
                  </audio>
                </div>
              )}

              {(error || audioUrl) && (
                <button
                  type="button"
                  onClick={handleRetry}
                  className="retry-button"
                >
                  Start Over
                </button>
              )}
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default VoiceSynthesizer;
