import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import { Mic, Upload, Download, AlertCircle, CheckCircle2 } from 'lucide-react';
import './voice-synthesisor.css';

const supabase = createClient('https://qgpgtosfopmnkrzcirfe.supabase.co', 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InFncGd0b3Nmb3BtbmtyemNpcmZlIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzE3Nzg2NzgsImV4cCI6MjA0NzM1NDY3OH0.w3M3ISoXlm2Ls6AFOQzPUOTuykYzr3xTLPYHkdd28bE');

const MAX_FILE_SIZE = 5 * 1024 * 1024; // 5MB
const ALLOWED_FILE_TYPES = ['audio/wav', 'audio/mpeg', 'audio/mp3', 'audio/mp4'];

export default function App() {
  const [voiceFile, setVoiceFile] = useState(null);
  const [text, setText] = useState('');
  const [generatedAudioUrl, setGeneratedAudioUrl] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);

  const validateFile = (file) => {
    if (!file) return 'Please select a file';
    if (!ALLOWED_FILE_TYPES.includes(file.type)) {
      return 'Invalid file format. Please upload an audio file (WAV, MP3, or MP4)';
    }
    if (file.size > MAX_FILE_SIZE) {
      return 'File is too large. Maximum size is 5MB';
    }
    return null;
  };

  const handleFileUpload = (event) => {
    const file = event.target.files?.[0];
    if (file) {
      const error = validateFile(file);
      if (error) {
        setError(error);
        setVoiceFile(null);
        event.target.value = '';
      } else {
        setVoiceFile(file);
        setError('');
        setSuccess('File uploaded successfully!');
      }
    }
  };

  const handleTextChange = (event) => {
    const newText = event.target.value;
    if (newText.length <= 500) {
      setText(newText);
      setError('');
    }
  };

  const simulateProgress = () => {
    setProgress(0);
    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev >= 100) {
          clearInterval(interval);
          return 100;
        }
        return prev + 10;
      });
    }, 500);
    return interval;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    if (!voiceFile) {
      setError('Please upload a voice file');
      setIsLoading(false);
      return;
    }
    if (!text.trim()) {
      setError('Please enter some text');
      setIsLoading(false);
      return;
    }

    const progressInterval = simulateProgress();

    try {
      // Upload voice file to Supabase storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('voice-files')
        .upload(`input/${Date.now()}-${voiceFile.name}`, voiceFile);

      if (uploadError) throw new Error('Failed to upload voice file');

      // Call backend API to handle voice synthesis
      const response = await fetch('http://localhost:3001/api/synthesize', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          voiceFileUrl: uploadData.path,
          text: text
        }),
      });

      if (!response.ok) throw new Error('Voice synthesis failed');

      const { audioUrl } = await response.json();
      setGeneratedAudioUrl(audioUrl);
      setSuccess('Voice synthesis completed successfully!');
      clearInterval(progressInterval);
      setProgress(100);
    } catch (error) {
      setError(error instanceof Error ? error.message : 'An unexpected error occurred');
      clearInterval(progressInterval);
      setProgress(0);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <div className="card-header">
          <h1 className="card-title">
            <Mic />
            Voice Synthesis App
          </h1>
          <p className="card-description">
            Upload a voice recording and enter text to generate synthesized speech
          </p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="voiceFile" className="form-label">
              Upload Voice File
            </label>
            <div className="flex items-center gap-10">
              <input
                id="voiceFile"
                type="file"
                accept={ALLOWED_FILE_TYPES.join(',')}
                onChange={handleFileUpload}
                className="form-input"
              />
              {voiceFile && <CheckCircle2 color="green" />}
            </div>
            <p className="mt-10">
              Maximum file size: 5MB. Supported formats: WAV, MP3, MP4
            </p>
          </div>

          <div className="form-group">
            <label htmlFor="text" className="form-label">
              Enter Text
            </label>
            <textarea
              id="text"
              value={text}
              onChange={handleTextChange}
              placeholder="Enter the text you want to synthesize (max 500 characters)"
              className="form-textarea"
            />
            <p className="mt-10">
              {text.length}/500 characters
            </p>
          </div>

          {error && (
            <div className="alert alert-error">
              <AlertCircle />
              <h4>Error</h4>
              <p>{error}</p>
            </div>
          )}

          {success && (
            <div className="alert alert-success">
              <CheckCircle2 />
              <h4>Success</h4>
              <p>{success}</p>
            </div>
          )}

          {isLoading && (
            <div className="mt-10">
              <div className="progress-bar">
                <div className="progress-bar-fill" style={{ width: `${progress}%` }}></div>
              </div>
              <p className="text-center mt-10">
                Processing your request... {progress}%
              </p>
            </div>
          )}

          <button
            type="submit"
            disabled={isLoading}
            className="button w-full"
          >
            {isLoading ? 'Processing...' : 'Generate Voice'}
          </button>
        </form>
        {generatedAudioUrl && (
          <div className="mt-10">
            <h3 className="form-label">Preview</h3>
            <audio controls src={generatedAudioUrl} className="w-full" />
            <button
              className="button button-outline w-full mt-10"
              onClick={() => {
                const link = document.createElement('a');
                link.href = generatedAudioUrl;
                link.download = 'synthesized-voice.mp3';
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
              }}
            >
              <Download className="mr-2" />
              Download Audio
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
