import React, { useState, useRef } from 'react';
import { Loader2, Upload, Volume2, AlertCircle } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

const VoiceSynthesizer = () => {
  const [text, setText] = useState('');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [audioUrl, setAudioUrl] = useState('');
  const [uploadProgress, setUploadProgress] = useState(0);
  const fileInputRef = useRef(null);
  const audioRef = useRef(null);

  const validateFile = (file) => {
    const validTypes = ['audio/wav', 'audio/mpeg', 'audio/mp3', 'audio/mp4'];
    if (!validTypes.includes(file.type)) {
      throw new Error('Invalid file type. Please upload an audio file (WAV, MP3, or MP4).');
    }
    if (file.size > 5 * 1024 * 1024) {
      throw new Error('File size too large. Maximum size is 5MB.');
    }
  };

  const handleFileChange = (event) => {
    try {
      const selectedFile = event.target.files[0];
      if (selectedFile) {
        validateFile(selectedFile);
        setFile(selectedFile);
        setError('');
      }
    } catch (err) {
      setError(err.message);
      setFile(null);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const checkFileStatus = async (fileName) => {
    try {
      const response = await fetch(`http://localhost:3001/api/check-file/${fileName}`);
      const data = await response.json();

      if (data.exists && data.url) {
        setAudioUrl(data.url);
        setLoading(false);
        return true;
      }
      return false;
    } catch (err) {
      console.error('Error checking file status:', err);
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text || !file) {
      setError('Both text and voice file are required.');
      return;
    }

    setLoading(true);
    setError('');
    setAudioUrl('');
    setUploadProgress(0);

    const formData = new FormData();
    formData.append('voiceFile', file);
    formData.append('text', text);

    try {
      const response = await fetch('http://localhost:3001/api/synthesize', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to synthesize audio');
      }

      const data = await response.json();

      // Check if the file exists and is ready
      let retries = 0;
      const maxRetries = 10;
      const fileName = data.fileName.split('/').pop();

      const checkFile = async () => {
        if (retries >= maxRetries) {
          throw new Error('Failed to generate audio file after multiple attempts');
        }

        const exists = await checkFileStatus(fileName);
        if (!exists) {
          retries++;
          await new Promise(resolve => setTimeout(resolve, 1000));
          return checkFile();
        }
      };

      await checkFile();

    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const handleRetry = () => {
    setError('');
    setAudioUrl('');
    setFile(null);
    setText('');
    setUploadProgress(0);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-2xl">
      <Card>
        <CardHeader>
          <CardTitle>Voice Synthesizer</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">
                Upload Voice File (WAV, MP3, or MP4, max 5MB)
              </label>
              <Input
                ref={fileInputRef}
                type="file"
                accept=".wav,.mp3,.mp4"
                onChange={handleFileChange}
                className="w-full"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-2">
                Text to Synthesize
              </label>
              <Textarea
                value={text}
                onChange={(e) => setText(e.target.value)}
                placeholder="Enter the text you want to synthesize..."
                className="w-full h-32"
              />
            </div>

            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertTitle>Error</AlertTitle>
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            <div className="space-y-4">
              <Button
                type="submit"
                disabled={loading || !file || !text}
                className="w-full"
              >
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Processing...
                  </>
                ) : (
                  <>
                    <Upload className="mr-2 h-4 w-4" />
                    Synthesize Audio
                  </>
                )}
              </Button>

              {uploadProgress > 0 && uploadProgress < 100 && (
                <div className="w-full bg-gray-200 rounded-full h-2.5">
                  <div
                    className="bg-blue-600 h-2.5 rounded-full"
                    style={{ width: `${uploadProgress}%` }}
                  ></div>
                </div>
              )}

              {audioUrl && (
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <Volume2 className="h-4 w-4" />
                    <span className="text-sm font-medium">Generated Audio</span>
                  </div>
                  <audio
                    ref={audioRef}
                    controls
                    className="w-full"
                    src={audioUrl}
                  >
                    Your browser does not support the audio element.
                  </audio>
                </div>
              )}

              {(error || audioUrl) && (
                <Button
                  type="button"
                  onClick={handleRetry}
                  variant="outline"
                  className="w-full"
                >
                  Start Over
                </Button>
              )}
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
};

export default VoiceSynthesizer;
